import React, { Component } from 'react';
import './App.css';
import Game from './Game/Game.js';

class App extends Component {

  state = {
    round: 1,
    games: [
      {homeTeam: "Team 1", awayTeam: "Team 2"},
      {homeTeam: "Team 3", awayTeam: "Team 4"},
      {homeTeam: "Team 4", awayTeam: "Team 5"}
    ]
  }

  render() {
    return (
      <div className="App">
        <div className="titleBar">
          <h2>Open Men • Monday • Division 2</h2>
          <button className="next">Next Round</button>
        </div>

        <div className="drawContainer">

          <div className="round">         
            <div className="roundDate">
              <h2>Round {this.state.round}</h2>
            </div>

            <div className="gamesList">
                <Game 
                  homeTeam= {this.state.games[0].homeTeam}
                  awayTeam= {this.state.games[0].awayTeam}
                  />
                <Game 
                  homeTeam= {this.state.games[1].homeTeam}
                  awayTeam= {this.state.games[1].awayTeam}
                />
                <Game 
                  homeTeam= {this.state.games[2].homeTeam}
                  awayTeam= {this.state.games[3].awayTeam}
                />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
