import React, { Component } from 'react';
import './App.css';
import Game from './Game/Game.js';

class App extends Component {

  state = {
    round: 1,
    firstRound: true,
    lastRound: false,
    games: [
      {homeTeam: "Team 1", awayTeam: "Team 2"},
      {homeTeam: "Team 3", awayTeam: "Team 4"},
      {homeTeam: "Team 4", awayTeam: "Team 5"}
    ]
  }

  switchRoundHandler = () => {
      this.setState({
        round: 2,
        games: [
          {homeTeam: "Team 3", awayTeam: "Team 1"},
          {homeTeam: "Team 2", awayTeam: "Team 5"},
          {homeTeam: "Team 6", awayTeam: "Team 4"}
        ]
      })
  }

  nextRoundHandler = () => {  
    let newRound = this.state.round + 1
    this.setState({
      round: newRound
    })
    if(newRound != 1) {
      this.setState({
        firstRound: false
      })
    }
  }


  previousRoundHandler = () => {  
    let newRound = this.state.round - 1
    this.setState({
      round: newRound
    })
    if(newRound === 1) {
      this.setState({
        firstRound: true
      })
    }
  }

  render() {

    let drawButtons = null;

    if (this.state.firstRound) {
      drawButtons = (
        <div>
          <button onClick={this.nextRoundHandler}>Next Round</button>
        </div>
      );
    } else if(this.state.lastRound) {
      drawButtons = (
        <div>
          <button onClick={this.previousRoundHandler}>Previous Round</button>
        </div>
      );
    } else {
      drawButtons = (
        <div>
          <button onClick={this.previousRoundHandler}>Previous Round</button>
          <button onClick={this.nextRoundHandler}>Next Round</button>
       </div>
      );
    }


    return (
      <div className="App">
        <div className="titleBar">
          <h2>Open Men • Monday • Division 2</h2>


        </div>

        <div className="drawContainer">

          <div className="round">         
            <div className="roundDate">
              <h2>Round {this.state.round}</h2>
            </div>
            {drawButtons}

            <div className="gamesList">
                <Game 
                  homeTeam= {this.state.games[0].homeTeam}
                  awayTeam= {this.state.games[0].awayTeam}
                  />
                <Game 
                  homeTeam= {this.state.games[1].homeTeam}
                  awayTeam= {this.state.games[1].awayTeam}
                />
                <Game 
                  homeTeam= {this.state.games[2].homeTeam}
                  awayTeam= {this.state.games[2].awayTeam}
                />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
